<?php

namespace App;

use Illuminate\Foundation\Auth\User as Authenticatable;
use Jenssegers\Date\Date;
use Illuminate\Database\Eloquent\SoftDeletes;

class Admin extends Authenticatable
{
    use SoftDeletes;

    /**
     * The attributes that are mass assignable.
     *
     * @var array
     */
    protected $fillable = [
        'name', 'email', 'password', 'level',
    ];

    /**
     * The attributes excluded from the model's JSON form.
     *
     * @var array
     */
    protected $hidden = [
        'password', 'remember_token',
    ];

    protected $level_display = [
        '1' => '管理员',
        '2' => '超级管理员',
        '3' => '销售部',
        '4' => '销售管理',
        '5' => '财务管理',
        '99' => '最高级管理员',
    ];

    /**
     * Format status field from tinyInt to string
     * @relationship  before:after = tinyInt:string
     * @return  mixed
     */
    public function getLevelDisplayAttribute()
    {
        if (isset($this->level_display[$this->attributes['level']])) {
            return $this->level_display[$this->attributes['level']];
        }
        return $this->unknown;
    }

    protected function asDateTime($value) {
        return new Date(parent::asDateTime($value));
    }

    public function getDefaultAvatarAttribute()
    {
        if ($this->isSuperAdmin()) {
            return "data:image/png;base64,";
        } else if ($this->isSeniorAdmin()) {
            return "data:image/jpeg;base64,";
        } else {
            return "data:image/jpeg;base64,";
        }
    }

    public function isSeniorAdmin() {
        return $this->attributes['level'] >= 2;
    }

    public function isNormalAdmin() {
        return $this->attributes['level'] >= 1;
    }

    public function isSuperAdmin() {
        return $this->attributes['level'] == 99;
    }
}
